# Create output folder
os.makedirs("pdf_documents", exist_ok=True)

def create_pdf(doc_id, content):
    filename = f"pdf_documents/document_{doc_id}.pdf"
    c = canvas.Canvas(filename, pagesize=A4)
    width, height = A4

    # Formatting
    c.setFont("Times-Roman", 12)
    margin = 50
    line_height = 16
    y = height - margin

    # Wrap the text to fit within the margins
    wrapped_text = wrap(content, width=95)

    for line in wrapped_text:
        if y < margin:
            c.showPage()
            c.setFont("Times-Roman", 12)
            y = height - margin
        c.drawString(margin, y, line)
        y -= line_height

    c.save()
    print(f"✅ Saved: {filename}")

# Generate PDFs
for doc in documents:
    create_pdf(doc["id"], doc["text"])
